        // Function to generate the report based on selected conditions
        function generateReport() {
            var condition1 = document.getElementById('category1').value;
            var condition2 = document.getElementById('category2').value;
            var condition3 = document.getElementById('category3').value;
            var condition4 = document.getElementById('category4').value;

            var filteredData = reportData.filter(function(item) {
                return item.condition1 === condition1 && item.condition2 === condition2 && item.condition3 === condition3 && item.condition4 === condition4;
                // return condition1.style.display === 'none'
            });

            renderReport(filteredData);
        }

        // Function to render the report in the HTML
        function renderReport(reportData) {
            var tableContainer = document.getElementById('tableContainer');
            tableContainer.innerHTML = '';

            if (reportData.length === 0) {
                tableContainer.innerHTML = 'No data found for selected conditions.';
                return;
            }

            var table = document.createElement('table');
            table.id = 'myTable';
            var thead = document.createElement('thead');
            table.appendChild(thead);

            var headerRow = thead.insertRow();
            headerRow.id = 'headerRow';
            Object.keys(reportData[0]).forEach(function(key) {
                var th = document.createElement('th');
                th.textContent = key;
                // th.style.display = 'none';
                headerRow.appendChild(th);
            });
            var tbody = document.createElement('tbody');
            tbody.id = 'tbody';
            table.appendChild(tbody);

            // Create and append rows and cells to the tbody
            reportData.forEach(function(rowData, rowIndex) {
                var row = tbody.insertRow();
                row.id = 'row' + rowIndex;
                row.class = 'delete-btn';

                Object.values(rowData).forEach(function(cellData, cellIndex) {
                    var cell = row.insertCell();
                    cell.textContent = cellData;
                    cell.id = 'cell' + rowIndex + '-' + cellIndex;
                });
            });
            reportData.forEach(function(item) {
                var row = table.insertRow();
                Object.values(item).forEach(function(value) {
                    var cell = row.insertCell();
                    cell.textContent = value;
                });
                row.addEventListener('click', function() {
                    // Deselect any previously selected row
                    var previouslySelected = tbody.querySelector('.selected');
                    if (previouslySelected) {
                        previouslySelected.classList.remove('selected');
                    }
                    // Select the clicked row
                    row.classList.add('selected');
                });
            });

            tableContainer.appendChild(table);
        }

        // Initially generate the report based on default selected conditions
        generateReport();

        function deleteSelectedRow() {
            var table = document.getElementById('myTable');
            var selectedRow = table.querySelector('.selected');
            if (selectedRow) {
                selectedRow.remove();
            } else {
                alert('Please select a row to delete.');
            }
        }

        // Add event listener to the delete button
        document.getElementById('deleteButton').addEventListener('click', deleteSelectedRow);

        // Call the function to create and populate the table with the example data
        generateReport(reportData);
    </script>

